import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../App';

function Navbar() {
  const { token, isAdmin, handleLogout } = useContext(AuthContext);

  return (
    <nav>
      <Link to="/">Home</Link> | <Link to="/cart">Cart</Link> |
      {!token ? (
        <>
          <Link to="/login">Login</Link> | <Link to="/signup">Signup</Link>
        </>
      ) : (
        <>
          {isAdmin ? <Link to="/admin-dashboard">Admin Dashboard</Link> : null} | <Link to="/products">Products</Link> | <Link to="/search-products">Search Products</Link> | <button onClick={handleLogout}>Logout</button>
        </>
      )}
    </nav>
  );
}

export default Navbar;
