import { useContext } from 'react';
import { AuthContext } from '../App';

function Cart() {
  const { token, cart, setCart } = useContext(AuthContext);

  const handleRemoveFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const handleIncreaseQuantity = (id) => {
    setCart(cart.map(item =>
      item.id === id ? { ...item, quantity: item.quantity + 1 } : item
    ));
  };

  const handleDecreaseQuantity = (id) => {
    setCart(cart.map(item =>
      item.id === id && item.quantity > 1 ? { ...item, quantity: item.quantity - 1 } : item
    ));
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (!token) return <div>Please log in to view your cart.</div>;

  return (
    <div>
      <h2>Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty. Add items from Home!</p>
      ) : (
        <>
          {cart.map((item) => (
            <div key={item.id} style={{ border: '1px solid #ccc', padding: '1rem', margin: '1rem' }}>
              <img src={item.image} alt={item.name} style={{ width: '100px' }} />
              <h3>{item.name}</h3>
              <p>₹{item.price} x {item.quantity}</p>
              <button onClick={() => handleDecreaseQuantity(item.id)}>-</button>
              <button onClick={() => handleIncreaseQuantity(item.id)}>+</button>
              <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
            </div>
          ))}
          <h3>Total: ₹{total.toFixed(2)}</h3>
          <button onClick={() => alert('Proceed to Checkout (TBD)')}>Checkout</button>
        </>
      )}
    </div>
  );
}

export default Cart;
