import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../App';

function Navbar() {
  const { token, isAdmin, handleLogout } = useContext(AuthContext);

  return (
    <nav className="bg-gradient-to-r from-blue-500 to-purple-600 p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-white text-xl font-bold">Blinkit Clone</div>
        <div className="space-x-4">
          <Link to="/" className="text-white hover:text-yellow-300">Home</Link>
          <Link to="/cart" className="text-white hover:text-yellow-300">Cart</Link>
          {!token ? (
            <>
              <Link to="/login" className="text-white hover:text-yellow-300">Login</Link>
              <Link to="/signup" className="text-white hover:text-yellow-300">Signup</Link>
              <Link to="/admin-login" className="text-white hover:text-yellow-300">Admin Login</Link>
            </>
          ) : (
            <>
              {isAdmin ? <Link to="/admin-dashboard" className="text-white hover:text-yellow-300">Admin Dashboard</Link> : null}
              <Link to="/products" className="text-white hover:text-yellow-300">Products</Link>
              <Link to="/search-products" className="text-white hover:text-yellow-300">Search Products</Link>
              <button onClick={handleLogout} className="btn btn-danger">Logout</button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
