import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../App';

function AdminLogin() {
  const { setToken, setIsAdmin } = useContext(AuthContext);
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Clear previous errors
    try {
      const res = await axios.post('http://localhost:5001/api/admin-login', credentials, {
        headers: { 'Content-Type': 'application/json' },
      });
      const { token, isAdmin } = res.data;
      setToken(token);
      setIsAdmin(isAdmin);
      localStorage.setItem('token', token);
      navigate('/admin-dashboard');
    } catch (err) {
      setError('Admin login failed: ' + (err.response?.data?.error || err.message));
      console.error('Admin login error:', err.response?.data || err);
    }
  };

  return (
    <div className="py-8">
      <h2 className="text-3xl font-bold text-center text-blue-700 mb-6">Admin Login</h2>
      {error && <p className="text-center text-red-500">{error}</p>}
      <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg">
        <div className="mb-4">
          <label className="block text-gray-700">Email</label>
          <input
            type="text"
            name="username"
            value={credentials.username}
            onChange={handleChange}
            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Password</label>
          <input
            type="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <button type="submit" className="btn btn-secondary w-full">Login</button>
      </form>
    </div>
  );
}

export default AdminLogin;
