import { useContext } from 'react';
import { AuthContext } from '../App';
import ProductCard from '../components/ProductCard';

function Home() {
  const { token } = useContext(AuthContext);
  const products = [
    { id: 1, name: 'Banana', price: 40.00, image: 'https://via.placeholder.com/100?text=Banana', category: 'Fruits' },
    { id: 2, name: 'Apple', price: 50.00, image: 'https://via.placeholder.com/100?text=Apple', category: 'Fruits' },
    { id: 3, name: 'Orange', price: 60.00, image: 'https://via.placeholder.com/100?text=Orange', category: 'Fruits' },
  ];

  return (
    <div className="py-8">
      <h2 className="text-3xl font-bold text-center text-blue-700 mb-6">Welcome to Blinkit Clone</h2>
      {token ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {products.map((product) => (
            <ProductCard
              key={product.id}
              id={product.id}
              name={product.name}
              price={product.price}
              image={product.image}
            />
          ))}
        </div>
      ) : (
        <p className="text-center text-red-500 text-lg">Please log in to view products.</p>
      )}
    </div>
  );
}

export default Home;
