import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../App';

function Navbar() {
  const { token, setToken, isAdmin } = useContext(AuthContext);

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <nav className="bg-blue-700 p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="text-white text-2xl font-bold">
          <Link to="/">Blinkit Clone</Link>
        </div>
        <div className="space-x-4">
          <Link to="/" className="text-white hover:text-blue-200">Home</Link>
          <Link to="/products" className="text-white hover:text-blue-200">Products</Link>
          {token ? (
            <>
              {isAdmin && <Link to="/admin-dashboard" className="text-white hover:text-blue-200">Admin</Link>}
              <button onClick={handleLogout} className="text-white hover:text-blue-200">Logout</button>
            </>
          ) : (
            <>
              <Link to="/signup" className="text-white hover:text-blue-200">Sign Up</Link>
              <Link to="/login" className="text-white hover:text-blue-200">Login</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
