import { useState, useEffect } from 'react';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import ProductCard from '../Components/ProductCard';

function Home() {
  const { token } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Attempt to fetch with token if available, fall back to public fetch if unauthorized
        let res;
        try {
          res = await axios.get('http://localhost:5001/api/admin/products', {
            headers: token ? { Authorization: `Bearer ${token}` } : {},
          });
        } catch (authErr) {
          if (authErr.response?.status === 401) {
            // If unauthorized, fetch without token (assuming a public endpoint or fallback)
            res = await axios.get('http://localhost:5001/api/products'); // Add public endpoint if available
          } else {
            throw authErr;
          }
        }
        setProducts(res.data);
      } catch (err) {
        setError('Failed to fetch products');
        console.error('Products fetch error:', err);
      }
    };
    fetchProducts();
  }, [token]);

  const handleAddToCart = async (productId) => {
    if (!token) {
      alert('Please log in to add products to cart');
      return;
    }
    try {
      await axios.post(
        'http://localhost:5001/api/cart',
        { product_id: productId, quantity: 1 },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Product added to cart!');
    } catch (err) {
      alert('Failed to add to cart');
      console.error('Add to cart error:', err);
    }
  };

  if (error) return <div className="text-center text-red-500 text-lg mt-8">{error}</div>;

  return (
    <div className="py-8">
      <h2 className="text-3xl font-bold text-center text-blue-700 mb-6">Welcome to Blinkit Clone</h2>
      {products.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto px-4">
          {products.map((product) => (
            <ProductCard
              key={product.id}
              product={product}
              onAddToCart={handleAddToCart}
            />
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-500 text-lg">Loading products...</p>
      )}
    </div>
  );
}

export default Home;
