import { useContext } from 'react';
import { AuthContext } from '../App';
import ProductCard from '../components/ProductCard';

function Home() {
  const { token } = useContext(AuthContext);
  const products = [
    { id: 1, name: 'Banana', price: 40.00, image: 'https://via.placeholder.com/100?text=Banana', category: 'Fruits' },
    { id: 2, name: 'Apple', price: 50.00, image: 'https://via.placeholder.com/100?text=Apple', category: 'Fruits' },
  ];

  const handleAddToCart = (product) => {
    console.log('Added to cart:', product.name);
  };

  return (
    <div>
      <h2>Home</h2>
      {token ? (
        <div>
          {products.map((product) => (
            <ProductCard
              key={product.id}
              name={product.name}
              price={product.price}
              image={product.image}
              onAddToCart={() => handleAddToCart(product)}
            />
          ))}
        </div>
      ) : (
        <p>Please log in to view products.</p>
      )}
    </div>
  );
}

export default Home;
